class PostComments{constructor(e){this.postId=e,this.postContainer=$("#post-"+e),this.newCommentForm=$(`#post-${e}-comment-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(n){n.preventDefault();$.ajax({type:"post",url:"/comment/create?id="+e,data:$(this).serialize(),success:function(n){let o=t.newCommentDom(n.data.comment,n.data.username);$("#post-comments-"+e).prepend(o),t.deleteComment($(" .delete-comment-button",o)),new ToggleLike($(" .toggle-like-button",o)),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e,t){return $(`<li id="comment-${e._id}">\n                        <p>\n                            \n                            <small>\n                                <a class="delete-comment-button" href="/comment/delete?id=${e._id}">X</a>\n                            </small>\n\n                            <a class="toggle-like-button" data-likes="0" href="/like/toggle?id=${e._id}&type=Comment">\n                                    0 Likes\n                            </a>\n                            \n                            ${e.content}\n                            <br>\n                            <small>\n                                ${t}\n                            </small>\n                            \n\n                        </p>    \n\n                </li>`)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){console.log(e),$("#comment-"+e.data.commentId).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}